import React from "react";
import { Container, Row, Col, Card } from "react-bootstrap";
import { FaFire, FaClipboardCheck, FaTools, FaBell, FaSyncAlt, FaChartLine } from "react-icons/fa";
import "../styles/Services.css";
import installImage from "../assets/installation.webp";
import trainingImage from "../assets/training.webp";
import maintenanceImage from "../assets/maintenance.webp";
import alarmImage from "../assets/alarm.webp";
import refillImage from "../assets/refill.webp";
import consultingImage from "../assets/consulting.webp";

function Services() {
  const services = [
    {
      icon: <FaFire />,
      image: fireSafetyInstallation,
      title: "Fire Safety Installation",
      description: "Expert installation of <strong>fire hydrants, alarms, and extinguishers</strong>. We ensure your business is <strong>fully prepared</strong> for any fire emergency.",
    },
    {
      icon: <FaClipboardCheck />,
      image: fireSafetyAudits,
      title: "Fire Safety Audits & Training",
      description: "Comprehensive <strong>safety audits, compliance checks</strong>, and <strong>hands-on training programs</strong> to empower your organization with fire safety expertise.",
    },
    {
      icon: <FaTools />,
      image: equipmentMaintenance,
      title: "Equipment Maintenance & Inspection",
      description: "Thorough <strong>maintenance and inspection</strong> of fire-fighting equipment, ensuring all systems are <strong>functional</strong> and meet safety standards.",
    },
    {
      icon: <FaBell />,
      image: fireAlarmInstallation,
      title: "Fire Alarm System Installation",
      description: "Top-tier <strong>fire alarm installations</strong>, including smoke detectors, manual call points, and beam detector panels for <strong>reliable protection</strong>.",
    },
    {
      icon: <FaSyncAlt />,
      image: fireExtinguisherRefilling,
      title: "Fire Extinguisher Refilling",
      description: "Efficient <strong>refilling and servicing</strong> of fire extinguishers to maintain <strong>compliance</strong> and ensure readiness for emergencies.",
    },
    {
      icon: <FaChartLine />,
      image: fireSafetyConsulting,
      title: "Fire Safety Consulting",
      description: "Specialized <strong>fire safety planning, risk assessment</strong>, and <strong>regulatory compliance</strong> guidance tailored for diverse industries.",
    },
  ];

  return (
    <div className="services-page">
      {/* Hero Section */}
      <div className="services-hero-section">
        <div className="hero-overlay">
          <Container className="hero-content">
            <h1>Your Trusted Fire Safety Partner</h1>
            <p>Comprehensive Solutions for Prevention and Protection.</p>
            <Button variant="danger" href="/contact" className="hero-button">
              Get Started Today
            </Button>
          </Container>
        </div>
      </div>

      {/* Services Section */}
      <Container fluid className="services-content-section">
        <Container>
          <h2 className="text-center mb-5">Our Expert Services</h2>
          <Row>
            {services.map((service, index) => (
              <Col md={4} className="mb-4" key={index}>
                <Card className="service-card">
                  <div className="service-image-wrapper">
                    <img src={service.image} alt={service.title} className="service-image" />
                  </div>
                  <Card.Body>
                    <div className="service-icon">{service.icon}</div>
                    <Card.Title>{service.title}</Card.Title>
                    <Card.Text>{service.description}</Card.Text>
                  </Card.Body>
                </Card>
              </Col>
            ))}
          </Row>
        </Container>
      </Container>
    </div>
  );
}

export default Services;
