import React from "react";
import { Container, Row, Col, Card } from "react-bootstrap";
import "../styles/Services.css";

function Services() {
  const services = [
    {
      title: "üî• Fire Safety Installation",
      description:
        "Complete installation of fire hydrants, alarm systems, and extinguishers. Ensuring businesses are fully equipped for fire emergencies.",
    },
    {
      title: "üßØ Fire Safety Audits & Training",
      description:
        "Conducting safety audits, compliance checks, and hands-on fire safety training programs for organizations.",
    },
    {
      title: "üõ†Ô∏è Equipment Maintenance & Inspection",
      description:
        "Routine maintenance and inspection of fire fighting equipment, ensuring all systems are functional and up-to-date.",
    },
    {
      title: "üö® Fire Alarm System Installation",
      description:
        "High-quality fire alarm installations, including smoke detectors, manual call points, and beam detector panels.",
    },
    {
      title: "üîÑ Fire Extinguisher Refilling",
      description:
        "Refilling and servicing of fire extinguishers to maintain efficiency and compliance with fire safety regulations.",
    },
    {
      title: "üìä Fire Safety Consulting",
      description:
        "Expert guidance in fire safety planning, risk assessment, and regulatory compliance for various industries.",
    },
  ];

  return (
    <Container className="services-page">
      <h2 className="text-center">Our Services</h2>
      <p className="text-center tagline">
        "From prevention to protection, we cover it all."
      </p>

      <Row>
        {services.map((service, index) => (
          <Col md={6} key={index}>
            <Card className="service-card">
              <Card.Body>
                <Card.Title>{service.title}</Card.Title>
                <Card.Text>{service.description}</Card.Text>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
}

export default Services;
