import React from "react";
import { Container, Row, Col, Card } from "react-bootstrap";
import "../styles/Projects.css";

// Importing project images
import proj1 from "../assets/mgm_hospital.webp";
import proj2 from "../assets/prozone_mall.webp";
import proj3 from "../assets/lemon_tree.webp";
import proj4 from "../assets/brahma_suncity.webp";
import proj5 from "../assets/pvr_nashik.webp";
import proj6 from "../assets/drdo.webp";

function Projects() {
  const projects = [
    {
      title: "üè• MGM Hospital, Chhatrapati Sambhajinagar",
      description:
        "Installation of a full-scale fire hydrant and sprinkler system to enhance fire safety in the medical facility.",
      image: proj1,
    },
    {
      title: "üõçÔ∏è Prozone Mall, Chhatrapati Sambhajinagar",
      description:
        "Fire suppression system and fire alarm installation for the region‚Äôs largest commercial mall.",
      image: proj2,
    },
    {
      title: "üè® Lemon Tree Hotel, Pune",
      description:
        "Implementation of fire alarm systems and hydrant pumps in compliance with hotel safety regulations.",
      image: proj3,
    },
    {
      title: "üè¢ Brahma Sun City, Pune",
      description:
        "Installation of fire extinguishers, hose reels, and alarm systems for a high-rise residential complex.",
      image: proj4,
    },
    {
      title: "üé¨ PVR Multiplex, Nashik",
      description:
        "Complete fire safety solutions, including smoke detectors, sprinklers, and emergency exits compliance.",
      image: proj5,
    },
    {
      title: "üèõÔ∏è DRDO, Ministry of Defense",
      description:
        "Supply of fire fighting crew and hydrant system setup for a government defense research facility.",
      image: proj6,
    },
  ];

  return (
    <Container className="projects-page">
      <h2 className="text-center">Our Key Projects</h2>
      <p className="text-center tagline">
        "Delivering fire safety solutions for leading industries and institutions."
      </p>

      <Row>
        {projects.map((project, index) => (
          <Col md={4} key={index}>
            <Card className="project-card">
              <div className="project-image-container">
                <Card.Img variant="top" src={project.image} className="project-image" />
              </div>
              <Card.Body>
                <Card.Title>{project.title}</Card.Title>
                <Card.Text>{project.description}</Card.Text>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
}

export default Projects;
