import React from "react";
import { Carousel, Container } from "react-bootstrap";
import "../styles/ProjectCarousel.css"; // Style file

// Import images (Make sure you have these in the public/images folder)
import project1 from "../assets/project1.webp";
import project2 from "../assets/project2.webp";
import project3 from "../assets/project3.webp";

function ProjectCarousel() {
  const projects = [
    {
      image: project1,
      title: "MGM University Fire Safety Installation",
      description: "Complete fire safety system installation, ensuring top-tier protection for students and staff.",
    },
    {
      image: project2,
      title: "Prozone Mall Fire Hydrant System",
      description: "Advanced fire hydrant system for one of the largest malls in Marathwada.",
    },
    {
      image: project3,
      title: "Carnival Mariplex - Pune",
      description: "State-of-the-art fire suppression system installed for ultimate safety in entertainment spaces.",
    },
  ];

  return (
    <Container className="project-carousel my-5">
      <h2 className="text-center mb-4">Our Major Projects</h2>
      <Carousel interval={3000} fade>
        {projects.map((project, index) => (
          <Carousel.Item key={index}>
            <img className="d-block w-100 carousel-image" src={project.image} alt={project.title} />
            <Carousel.Caption>
              <h3>{project.title}</h3>
              <p>{project.description}</p>
            </Carousel.Caption>
          </Carousel.Item>
        ))}
      </Carousel>
    </Container>
  );
}

export default ProjectCarousel;
