import React from "react";
import { Card, Container, Row, Col } from "react-bootstrap";
import "../styles/Testimonials.css";

// Sample testimonials data
const testimonials = [
  {
    name: "Rahul Deshmukh",
    designation: "Facility Manager, Prozone Mall",
    feedback: "PBJ Fire’s fire hydrant system gave us complete peace of mind. Their expertise and efficiency were outstanding!",
  },
  {
    name: "Dr. Neha Patil",
    designation: "Safety Head, MGM Hospital",
    feedback: "Their fire safety audit was incredibly thorough. We now feel fully prepared for any emergency.",
  },
  {
    name: "Amit Kulkarni",
    designation: "Operations Manager, Carnival Mariplex",
    feedback: "The fire suppression system they installed at our multiplex is top-notch. Highly professional service!",
  },
];

function Testimonials() {
  return (
    <Container className="testimonials my-5">
      <h2 className="text-center mb-4">What Our Clients Say</h2>
      <Row className="justify-content-center">
        {testimonials.map((testimonial, index) => (
          <Col key={index} md={4} sm={12} className="mb-3">
            <Card className="testimonial-card">
              <Card.Body>
                <Card.Text className="feedback">"{testimonial.feedback}"</Card.Text>
                <Card.Title className="name">{testimonial.name}</Card.Title>
                <Card.Subtitle className="designation">{testimonial.designation}</Card.Subtitle>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
}

export default Testimonials;
